sysauto_script.script:
// Get today's time and date
var rightNow = new GlideDateTime();
var aDayFromNow = new GlideDateTime();
aDayFromNow.addDaysLocalTime(1);

// Query the database for NeedIt Task records with Due date field values older than the current time.
// Only return NeedIt Task records that do not have a State field value of Closed Complete.
var dueSoonNITasks = new GlideRecord('x_58872_needit_needit_task');
dueSoonNITasks.addQuery('due_date', '>=', rightNow);
dueSoonNITasks.addQuery('due_date', '<=', aDayFromNow);
dueSoonNITasks.addQuery('state', '!=', 3);

dueSoonNITasks.query();
// Write a log message for each overdue NeedIt Task Record
while(dueSoonNITasks.next()){
	gs.info("Due Soon NeedIt Task record = " + dueSoonNITasks.number);
	gs.eventQueue('x_58872_needit.dueSoonNeedItTasks', dueSoonNITasks, dueSoonNITasks.number, gs.getUserName());
}
